acts/mocks/aave/AAVELendingPool.sol":{"content":"pragma solidity =0.7.6;
pragma experimental ABIEncoderV2;

import './dependencies/token/ERC20/IERC20.sol';
import './interfaces/IAToken.sol';

import './dependencies/token/ERC20/SafeERC20.sol';

import {ILendingPool} from './interfaces/ILendingPool.sol';
import {IAaveFlashLoanReceiver} from './interfaces/IAaveFlashLoanReceiver.sol';

contract AAVELendingPool is ILendingPool {
    using SafeERC20 for IERC20;

    mapping(address => ILendingPool.ReserveData) internal _reserves;

    constructor() {}

    // helper
    function addReserve(address underlyingAsset, address aToken) external {
        _reserves[underlyingAsset].aTokenAddress = aToken;
    }

    function deposit(
        address underlyingAsset,
        uint256 amount,
        address onBehalfOf,
        uint16 referralCode
    ) external override {
        address aToken = _reserves[underlyingAsset].aTokenAddress;
        IERC20(underlyingAsset).safeTransferFrom(msg.sender, aToken, amount);

        IAToken(aToken).mint(onBehalfOf, amount);
    }

    function simulateInterestIncrease(
        address underlyingAsset,
        address onBehalfOf,
        uint256 amount
    ) external {
        address aToken = _reserves[underlyingAsset].aTokenAddress;
        IAToken(aToken).mint(onBehalfOf, amount);
    }

    function withdraw(
        address underlyingAsset,
        uint256 amount,
        address to
    ) external override returns (uint256) {
        uint256 amountToWithdraw = amount;

        address aToken = _reserves[underlyingAsset].aTokenAddress;
        IAToken(aToken).burn(msg.sender, to, amountToWithdraw);

        return amountToWithdraw;
    }

    function getReserveData(address underlyingAsset) public view override returns (ILendingPool.ReserveData memory) {
        return _reserves[underlyingAsset];
    }

    event FlashLoan(
        address indexed target,
        address indexed initiator,
        address indexed asset,
        uint256 amount,
        uint256 premium,
        uint16 referralCode
    );

    function flashLoan(
        address receiverAddress,
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata,
        address,
        bytes calldata params,
        uint16 referralCode
    ) external override {
        uint256[] memory premiums = new uint256[](assets.length);
        for (uint256 i = 0; i < assets.length; i++) {
            premiums[i] = (amounts[i] * 9) / 10000;
            IAToken(_reserves[assets[i]].aTokenAddress).transferUnderlyingTo(receiverAddress, amounts[i]);
        }

        require(
            IAaveFlashLoanReceiver(receiverAddress).executeOperation(assets, amounts, premiums, msg.sender, params),
            'AAVE_FLASH_LOAN_EXECUTOR_FAILED'
        );

        for (uint256 i = 0; i < assets.length; i++) {
            premiums[i] = (amounts[i] * 9) / 10000;
            IERC20(assets[i]).safeTransferFrom(receiverAddress, _reserves[assets[i]].aTokenAddress, amounts[i] + premiums[i]);
            emit FlashLoan(receiverAddress, msg.sender, assets[i], amounts[i], premiums[i], referralCode);
        }
    }
}
