pragma solidity 0.8.10;
import '../libraries/BespokeTypes.sol';

contract StrategyAnyInSet {
    function tokenIdMatchOffer(BespokeTypes.Offer memory offerData, BespokeTypes.LendOfferTakeParams memory takeInfo)
        external
        view
        returns (bool)
    {
        if (offerData.offerType != BespokeTypes.OfferType.MULTIPLE) return false;

        uint256[] memory tokenIds = abi.decode(takeInfo.params, (uint256[]));

        bool inSet = false;
        for (uint256 i = 0; i < tokenIds.length; i++) {
            if (tokenIds[i] == takeInfo.tokenId) {
                inSet = true;
                break;
            }
        }

        return inSet;
    }
}
