pragma solidity 0.8.10;

library BespokeTypes {
    struct Offer {
        OfferType offerType;
        bool isProrated;
        uint256 reserveId;
        address nftAddress;
        uint256 tokenId;
        uint256 tokenAmount; // 1 for ERC721, 1+ for ERC1155
        address signer; //borrower
        uint256 borrowAmountMin;
        uint256 borrowAmountMax;
        uint40 borrowDurationMin;
        uint40 borrowDurationMax;
        uint128 borrowRate;
        address currency;
        uint256 nonce;
        uint256 deadline;
        address strategy;
        bytes params;
        uint8 v; // v: parameter (27 or 28)
        bytes32 r; // r: parameter
        bytes32 s; // s: parameter
    }

    enum OfferType {
        BORROW, // borrow offer
        SINGLE, // single
        COLLECTION, // offer to collection
        MULTIPLE, // many. only for ERC721
        ATTRIBUTE, // by attribute
        PRIVATE // private
    }

    // params for takeLendOffer
    struct LendOfferTakeParams {
        address taker;
        uint256 tokenId;
        bytes params;
    }

    struct validateTakeLendOfferParams {
        bytes32 offerHash;
        address underlyingSpecified;
        uint256 borrowAmount;
        uint256 borrowDuration;
        bytes32 domainSeparator;
        address bespokeSettings;
        address settings;
    }

    struct executeTakeLendParams {
        uint256 tokenId;
        uint256 borrowAmount; // new borrow amount
        uint256 borrowDuration; // new borrow duration
        address bespokeSettings;
        address settings;
        bytes32 domainSeparator;
        bytes32 offerHash;
        bytes params;
    }

    struct executeTakeBorrowParams {
        uint256 amount;
        uint256 duration;
        address bespokeSettings;
        address settings;
        bytes32 domainSeparator;
        bytes32 offerHash;
    }

    // repayAmount?
    // adminFee
    struct LoanData {
        uint256 reserveId;
        address nftAddress;
        uint256 tokenId;
        uint256 tokenAmount; // 1 for ERC721, 1+ for ERC1155
        address borrower;
        uint256 amount;
        uint128 borrowRate;
        uint128 interestPerSecond;
        address currency;
        uint40 borrowDuration;
        // after take offer
        uint40 borrowBegin;
        uint40 borrowOverdueTime;
        uint40 liquidatableTime;
        address lender;
        LoanStatus status;
    }

    enum LoanStatus {
        NONE,
        BORROWING,
        OVERDUE,
        LIQUIDATABLE
    }

    struct WhitelistInfo {
        bool enabled;
