//SPDX-License-Identifier: Unlicense
pragma solidity 0.8.10;

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/token/ERC721/extensions/ERC721Enumerable.sol';
import '@openzeppelin/contracts/utils/Counters.sol';

contract OpenSkyERC721Mock is ERC721, ERC721Enumerable {
    using Counters for Counters.Counter;
    Counters.Counter private _tokenIds;

    event Minted(uint256 indexed itemId, address indexed owner);

    constructor(string memory name, string memory symbol) ERC721(name, symbol) {}

    function awardItem(address player) public returns (uint256) {
        _tokenIds.increment();

        uint256 newItemId = _tokenIds.current();
        _mint(player, newItemId);

        emit Minted(newItemId, player);

        return newItemId;
    }

    function currentValue() public view returns (uint256) {
        return _tokenIds.current();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override(ERC721, ERC721Enumerable) {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /**
     * @dev See {IERC165-supportsInterface}.
     */
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(ERC721, ERC721Enumerable)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

contracts/refinance/adapters/BespokeToBe.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol';
import '@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol';

import '../interfaces/IAaveFlashLoanReceiver.sol';
import '../interfaces/IAaveLendPoolAddressesProvider.sol';
import '../interfaces/IAaveLendPool.sol';

import '../../bespokemarket/interfaces/IOpenSkyBespokeSettings.sol';
import '../../bespokemarket/interfaces/IOpenSkyBespokeMarket.sol';
import '../../bespokemarket/libraries/BespokeTypes.sol';

contract BespokeToBespokeAdapter is IAaveFlashLoanReceiver, ERC721Holder, ERC1155Holder {
    using SafeERC20 for IERC20;

    IAaveLendPoolAddressesProvider public immutable AAVE2_ADDRESSES_PROVIDER;
    IOpenSkyBespokeSettings public immutable BESPOKE_SETTINGS;

    event RefinanceBespokeToBespoke(
        address indexed asset,
        address indexed borrower,
        uint256 oldLoanId,
        uint256 newLoanId
    );

    constructor(IAaveLendPoolAddressesProvider provider, IOpenSkyBespokeSettings bespokeSettings) {
        AAVE2_ADDRESSES_PROVIDER = provider;
        BESPOKE_SETTINGS = bespokeSettings;
    }

    function aavePool() public view returns (IAaveLendPool) {
        return IAaveLendPool(AAVE2_ADDRESSES_PROVIDER.getLendingPool());
    }

    struct LocalVars {
        uint256 loanId;
        uint256 repayAmount;
        address assetAddress;
        uint256 borrowAmount;
        uint256 borrowDuration;
        address borrower;
        uint256 needInput;
        bytes params;
        bytes params2;
        BespokeTypes.Offer offer;
        uint256 newLoanId;
    }

    function executeOperation(
        address[] calldata assets,
        uint256[] calldata amounts,
        uint256[] calldata premiums,
        address initiator,
        bytes calldata params
    ) external returns (bool) {
        require(
            assets.length == 1 && amounts.length == 1 && premiums.length == 1,
            'BM_FLASH_LOAN_MULTIPLE_ASSETS_NOT_SUPPORTED'
        );
        require(address(aavePool()) == msg.sender, 'BM_FLASH_LOAN_INVALID_CALLER');

        LocalVars memory vars;

        // decode
        (vars.params, vars.borrower) = abi.decode(params, (bytes, address));
        (vars.loanId, vars.borrowAmount, vars.borrowDuration, vars.params2) = abi.decode(
            vars.params,
            (uint256, uint256, uint256, bytes)
        );
        vars.offer = abi.decode(vars.params2, (BespokeTypes.Offer));

        // get loan data before repay
        BespokeTypes.LoanData memory loan = IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).getLoanData(
            vars.loanId
        );

        require(loan.currency == vars.offer.currency, 'BM_FLASH_LOAN_CURRENCY_NOT_MATCH');
        require(loan.tokenAddress == vars.offer.tokenAddress, 'BM_FLASH_LOAN_TOKEN_ADDRESS_MATCH');

        // OpenSkyBespokeBorrowNFT
        IERC721(BESPOKE_SETTINGS.borrowLoanAddress()).safeTransferFrom(vars.borrower, address(this), vars.loanId);

        // repay loan
        vars.repayAmount =
            IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).getPenalty(vars.loanId) +
            IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).getBorrowBalance(vars.loanId);

        require(vars.repayAmount <= amounts[0], 'BP_FLASH_LOAN_RECEIVE_MONEY_NOT_ENOUGH');

        IERC20(assets[0]).approve(BESPOKE_SETTINGS.marketAddress(), vars.repayAmount);
        IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).repay(vars.loanId);

        // borrow again
        // TODO: Need to support erc1155?
        IERC721(loan.tokenAddress).approve(BESPOKE_SETTINGS.getNftTransferAdapter(loan.tokenAddress), loan.tokenId);

        // only support single or colletcion offer
        vars.newLoanId = IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).takeLendOffer(
            vars.offer,
            loan.tokenId,
            vars.borrowAmount,
            vars.borrowDuration,
            address(this),
            ''
        );

        vars.needInput = vars.repayAmount + premiums[0];

        // transfer erc20
        if (vars.borrowAmount > vars.needInput) {
            IERC20(assets[0]).safeTransfer(vars.borrower, vars.borrowAmount - vars.needInput);
        } else if (vars.borrowAmount < vars.needInput) {
            IERC20(assets[0]).safeTransferFrom(vars.borrower, address(this), vars.needInput - vars.borrowAmount);
        }

        // transfer OpenSkyBespokeBorrowNFT
        IERC721(BESPOKE_SETTINGS.borrowLoanAddress()).safeTransferFrom(address(this), vars.borrower, vars.newLoanId);

        //repay flashloan
        IERC20(assets[0]).approve(address(aavePool()), amounts[0] + premiums[0]);

        emit RefinanceBespokeToBespoke(assets[0], vars.borrower, vars.loanId, vars.newLoanId);
        return true;
    }
}

contracts/bespokemarket/transferAdapters.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '@openzeppelin/contracts/utils/Context.sol';
//import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/token/ERC721/IERC721.sol';
import '@openzeppelin/contracts/token/ERC20/IERC20.sol';
import '@openzeppelin/contracts/token/ERC1155/IERC1155.sol';
import '@openzeppelin/contracts/security/ReentrancyGuard.sol';
import '@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol';
import '@openzeppelin/contracts/token/ERC1155/utils/ERC1155Holder.sol';
import '@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol';

import '../libraries/BespokeTypes.sol';
import '../../interfaces/IOpenSkySettings.sol';
import '../../interfaces/IACLManager.sol';
import '../../interfaces/IOpenSkyFlashClaimReceiver.sol';

import '../interfaces/IOpenSkyBespokeSettings.sol';
import '../interfaces/IOpenSkyBespokeMarket.sol';

import '../interfaces/ITransferAdapterCollateralBase.sol';
import '../interfaces/ITransferAdapterNFT.sol';

abstract contract TransferAdapterCollateralBase is
    Context,
    ReentrancyGuard,
    ERC721Holder,
    ERC1155Holder,
    ITransferAdapterCollateralBase,
    ITransferAdapterNFT
{
    using SafeERC20 for IERC20;

    IOpenSkySettings public immutable SETTINGS;
    IOpenSkyBespokeSettings public immutable BESPOKE_SETTINGS;

    // Add a protective layer to prevent collateral be claimed as airdrop
    // nftAddress=>amount
    mapping(address => uint256) public nftBorrowStat;

    constructor(address SETTINGS_, address BESPOKE_SETTINGS_) ReentrancyGuard() {
        SETTINGS = IOpenSkySettings(SETTINGS_);
        BESPOKE_SETTINGS = IOpenSkyBespokeSettings(BESPOKE_SETTINGS_);
    }

    modifier onlyMarket() {
        require(_msgSender() == BESPOKE_SETTINGS.marketAddress(), 'BM_ACL_ONLY_BESPOKR_MARKET_CAN_CALL');
        _;
    }

    /// @dev Only emergency admin can call functions marked by this modifier.
    modifier onlyEmergencyAdmin() {
        IACLManager ACLManager = IACLManager(SETTINGS.ACLManagerAddress());
        require(ACLManager.isEmergencyAdmin(_msgSender()), 'BM_ACL_ONLY_EMERGENCY_ADMIN_CAN_CALL');
        _;
    }

    modifier onlyAirdropOperator() {
        IACLManager ACLManager = IACLManager(SETTINGS.ACLManagerAddress());
        require(ACLManager.isAirdropOperator(_msgSender()), 'BM_ACL_ONLY_AIRDROP_OPERATOR_CAN_CALL');
        _;
    }

    /// @dev transfer ERC20 from the utility contract, for ERC20 recovery in case of stuck tokens due
    /// direct transfers to the contract address.
    /// @param token token to transfer
    /// @param to recipient of the transfer
    /// @param amount amount to send
    function emergencyTokenTransfer(
        address token,
        address to,
        uint256 amount
    ) external onlyEmergencyAdmin {
        IERC20(token).safeTransfer(to, amount);
    }

    function flashClaim(
        address receiverAddress,
        uint256[] calldata loanIds,
        bytes calldata params
    ) external override {
        uint256 i;
        IOpenSkyFlashClaimReceiver receiver = IOpenSkyFlashClaimReceiver(receiverAddress);
        // !!!CAUTION: receiver contract may reentry mint, burn, flashClaim again

        // only loan owner can do flashClaim
        address[] memory nftAddresses = new address[](loanIds.length);
        uint256[] memory tokenIds = new uint256[](loanIds.length);
        for (i = 0; i < loanIds.length; i++) {
            require(
                IERC721(BESPOKE_SETTINGS.borrowLoanAddress()).ownerOf(loanIds[i]) == _msgSender(),
                'BM_FLASHCLAIM_CALLER_IS_NOT_OWNER'
            );
            BespokeTypes.LoanData memory loanData = IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).getLoanData(
                loanIds[i]
            );
            require(loanData.status != BespokeTypes.LoanStatus.LIQUIDATABLE, 'BM_FLASHCLAIM_STATUS_ERROR');
            nftAddresses[i] = loanData.tokenAddress;
            tokenIds[i] = loanData.tokenId;
        }

        // step 1: moving underlying asset forward to receiver contract
        for (i = 0; i < loanIds.length; i++) {
            IERC721(nftAddresses[i]).safeTransferFrom(address(this), receiverAddress, tokenIds[i]);
        }

        // setup 2: execute receiver contract, doing something like aidrop
        require(
            receiver.executeOperation(nftAddresses, tokenIds, _msgSender(), address(this), params),
            'BM_FLASHCLAIM_EXECUTOR_ERROR'
        );

        // setup 3: moving underlying asset backword from receiver contract
        for (i = 0; i < loanIds.length; i++) {
            IERC721(nftAddresses[i]).safeTransferFrom(receiverAddress, address(this), tokenIds[i]);
            emit FlashClaim(receiverAddress, _msgSender(), nftAddresses[i], tokenIds[i]);
        }
    }

    function claimERC20Airdrop(
        address token,
        address to,
        uint256 amount
    ) external override nonReentrant onlyAirdropOperator {
        IERC20(token).safeTransfer(to, amount);
        emit ClaimERC20Airdrop(token, to, amount);
    }

    function claimERC721Airdrop(
        address token,
        address to,
        uint256[] calldata ids
    ) external override nonReentrant onlyAirdropOperator {
        require(nftBorrowStat[token] == 0, 'BM_CLAIM_ERC721_AIRDROP_NOT_SUPPORTED');
        for (uint256 i = 0; i < ids.length; i++) {
            IERC721(token).safeTransferFrom(address(this), to, ids[i]);
        }
        emit ClaimERC721Airdrop(token, to, ids);
    }

    function claimERC1155Airdrop(
        address token,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts,
        bytes calldata data
    ) external override nonReentrant onlyAirdropOperator {
        require(nftBorrowStat[token] == 0, 'BM_CLAIM_ERC1155_AIRDROP_NOT_SUPPORTED');
        IERC1155(token).safeBatchTransferFrom(address(this), to, ids, amounts, data);
        emit ClaimERC1155Airdrop(token, to, ids, amounts, data);
    }

    function transferCollateralIn(
        address collection,
        address from,
        uint256 tokenId,
        uint256 amount
    ) external override nonReentrant onlyMarket {
        nftBorrowStat[collection] += 1;
        _transferCollateralIn(collection, from, tokenId, amount);
    }

    function transferCollateralOut(
        address collection,
        address to,
        uint256 tokenId,
        uint256 amount
    ) external override nonReentrant onlyMarket {
        nftBorrowStat[collection] -= 1;
        _transferCollateralOut(collection, to, tokenId, amount);
    }

    function transferCollateralOutOnForeclose(
        address collection,
        address to,
        uint256 tokenId,
        uint256 amount
    ) public override nonReentrant onlyMarket {
        nftBorrowStat[collection] -= 1;
        _transferCollateralOutOnForeclose(collection, to, tokenId, amount);
    }

    function _transferCollateralIn(
        address collection,
        address from,
        uint256 tokenId,
        uint256 amount
    ) internal virtual;

    function _transferCollateralOut(
        address collection,
        address from,
        uint256 tokenId,
        uint256 amount
    ) internal virtual;

    function _transferCollateralOutOnForeclose(
        address collection,
        address from,
        uint256 tokenId,
        uint256 amount
    ) internal virtual;
}

contracts/bespokemarket/interfaces/ITran.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

interface ITransferAdapterCollateralBase {
    event FlashClaim(address indexed receiver, address sender, address indexed nftAddress, uint256 indexed tokenId);
    event ClaimERC20Airdrop(address indexed token, address indexed to, uint256 amount);
    event ClaimERC721Airdrop(address indexed token, address indexed to, uint256[] ids);
    event ClaimERC1155Airdrop(address indexed token, address indexed to, uint256[] ids, uint256[] amounts, bytes data);


    /**
 * @notice Allows smart contracts to access the collateralized NFT within one transaction,
     * as long as the amount taken plus a fee is returned
     * @dev IMPORTANT There are security concerns for developers of flash loan receiver contracts that must be carefully considered
     * @param receiverAddress The address of the contract receiving the funds, implementing IFlashClaimReceiver interface
     * @param loanIds The ID of loan being flash-borrowed
     * @param params packed params to pass to the receiver as extra information
     **/
    function flashClaim(
        address receiverAddress,
        uint256[] calldata loanIds,
        bytes calldata params
    ) external;

    /**
     * @notice Claim the ERC20 token which has been airdropped to the loan contract
     * @param token The address of the airdropped token
     * @param to The address which will receive ERC20 token
     * @param amount The amount of the ERC20 token
     **/
    function claimERC20Airdrop(
        address token,
        address to,
        uint256 amount
    ) external;

    /**
     * @notice Claim the ERC721 token which has been airdropped to the loan contract
     * @param token The address of the airdropped token
     * @param to The address which will receive the ERC721 token
     * @param ids The ID of the ERC721 token
     **/
    function claimERC721Airdrop(
        address token,
        address to,
        uint256[] calldata ids
    ) external;

    /**
     * @notice Claim the ERC1155 token which has been airdropped to the loan contract
     * @param token The address of the airdropped token
     * @param to The address which will receive the ERC1155 tokens
     * @param ids The ID of the ERC1155 token
     * @param amounts The amount of the ERC1155 tokens
     * @param data packed params to pass to the receiver as extra information
     **/
    function claimERC1155Airdrop(
        address token,
        address to,
        uint256[] calldata ids,
        uint256[] calldata amounts,
        bytes calldata data
    ) external;
}

