// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '@openzeppelin/contracts/token/ERC721/ERC721.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '@openzeppelin/contracts/utils/Context.sol';

import './libraries/BespokeTypes.sol';

import '../interfaces/IOpenSkyNFTDescriptor.sol';
import './interfaces/IOpenSkyBespokeSettings.sol';
import './interfaces/IOpenSkyBespokeLoanNFT.sol';
import './interfaces/IOpenSkyBespokeMarket.sol';

/**
 * @title OpenSkyBespokeLoanNFT contract
 * @author OpenSky Labs
 * @notice When a loan created, both lender and borrower will receive an NFT as receipt which represents the corresponding rights and interest
 **/
contract OpenSkyBespokeLoanNFT is Context, Ownable, ERC721, IOpenSkyBespokeLoanNFT {
    IOpenSkyBespokeSettings public immutable BESPOKE_SETTINGS;

    address public loanDescriptorAddress;

    modifier onlyMarket() {
        require(_msgSender() == BESPOKE_SETTINGS.marketAddress(), 'BM_ACL_ONLY_BESPOKR_MARKET_CAN_CALL');
        _;
    }

    constructor(
        string memory name,
        string memory symbol,
        address bespokeSettings_
    ) Ownable() ERC721(name, symbol) {
        BESPOKE_SETTINGS = IOpenSkyBespokeSettings(bespokeSettings_);
    }

    function setLoanDescriptorAddress(address address_) external onlyOwner {
        require(address_ != address(0));
        loanDescriptorAddress = address_;
        emit SetLoanDescriptorAddress(_msgSender(), address_);
    }

    function mint(uint256 tokenId, address account) external override onlyMarket {
        _safeMint(account, tokenId);
        emit Mint(tokenId, account);
    }

    function burn(uint256 tokenId) external onlyMarket {
        _burn(tokenId);
        emit Burn(tokenId);
    }

    function getLoanData(uint256 tokenId) public returns (BespokeTypes.LoanData memory) {
        return IOpenSkyBespokeMarket(BESPOKE_SETTINGS.marketAddress()).getLoanData(tokenId);
    }

    function tokenURI(uint256 tokenId) public view override returns (string memory) {
        if (loanDescriptorAddress != address(0)) {
            return IOpenSkyNFTDescriptor(loanDescriptorAddress).tokenURI(tokenId);
        } else {
            return '';
        }
    }
}

contracts/liquidation/OpenSkyDutchAuctio.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import "../interfaces/IOpenSkyDutchAuctionPriceOracle.sol";

contract OpenSkyDutchAuctionPriceOracle is IOpenSkyDutchAuctionPriceOracle {
    uint256 constant DURATION_ONE = 2 days;
    uint256 constant DURATION_TWO = 3 days;
    uint256 constant SPACING = 5 minutes; // price descend every 5 minutes

    function calculatePrice(
        uint256 startPrice,
        uint256 endPrice,
        uint256 startTime,
        uint256 endTime,
        uint256 priceTime
    ) public pure returns (uint256) {
        uint256 spacingAmount = (endTime - startTime) / SPACING;
        uint256 priceUint = (startPrice - endPrice) / spacingAmount;
        uint256 currentIndex = (priceTime - startTime) / SPACING;
        uint256 price = startPrice - currentIndex * priceUint;
        return price;
    }

    function getPrice(uint256 reservePrice, uint256 startTime) external view override returns (uint256) {
        // 10*loan => 3*loan=> 1.2*loan
        uint256 startPrice = reservePrice * 10;
        uint256 turningPrice = reservePrice * 3;
        uint256 endPrice = (reservePrice * 12000) / 10000;
        uint256 turnTime = startTime + DURATION_ONE;
        uint256 endTime = turnTime + DURATION_TWO;

        uint256 price = endPrice;

        if (block.timestamp <= startTime) {
            price = startPrice;
        } else if (block.timestamp <= turnTime) {
            // price = startPrice - ((block.timestamp - startTime) * (startPrice - turningPrice)) / DURATION_ONE;
            price = calculatePrice(startPrice, turningPrice, startTime, turnTime, block.timestamp);
        } else if (block.timestamp < endTime) {
            // price = turningPrice - ((block.timestamp - turnTime) * (turningPrice - endPrice)) / DURATION_TWO;
            price = calculatePrice(turningPrice, endPrice, turnTime, endTime, block.timestamp);
        } else {
            price = endPrice;
        }
        return price;
    }
}

contracts/configuration/ACLManager.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity 0.8.10;

import '@openzeppelin/contracts/access/AccessControl.sol';
import '@openzeppelin/contracts/access/Ownable.sol';
import '../interfaces/IACLManager.sol';

contract ACLManager is IACLManager, AccessControl, Ownable {
    bytes32 public constant POOL_ADMIN_ROLE = keccak256('POOL_ADMIN');

    bytes32 public constant EMERGENCY_ADMIN_ROLE = keccak256('EMERGENCY_ADMIN');

    bytes32 public constant GOVERNANCE_ROLE = keccak256('GOVERNANCE');

    bytes32 public constant LIQUIDATOR_ROLE = keccak256('LIQUIDATOR');

    bytes32 public constant LIQUIDATION_OPERATOR = keccak256('LIQUIDATION_OPERATOR');

    bytes32 public constant AIRDROP_OPERATOR = keccak256('AIRDROP_OPERATOR');

    constructor() Ownable() {
        _setupRole(DEFAULT_ADMIN_ROLE, owner());
    }

    function addEmergencyAdmin(address admin) external override {
        grantRole(EMERGENCY_ADMIN_ROLE, admin);
    }

    function isEmergencyAdmin(address admin) external view override returns (bool) {
        return hasRole(EMERGENCY_ADMIN_ROLE, admin);
    }

    function removeEmergencyAdmin(address admin) external override {
        revokeRole(EMERGENCY_ADMIN_ROLE, admin);
    }

    function addGovernance(address admin) external override {
        grantRole(GOVERNANCE_ROLE, admin);
    }

    function isGovernance(address admin) external view override returns (bool) {
        return hasRole(GOVERNANCE_ROLE, admin);
    }

    function removeGovernance(address admin) external override {
        revokeRole(GOVERNANCE_ROLE, admin);
    }

    function addPoolAdmin(address poolAdmin) external override {
        grantRole(POOL_ADMIN_ROLE, poolAdmin);
    }

    function isPoolAdmin(address poolAdmin) external view override returns (bool) {
        return hasRole(POOL_ADMIN_ROLE, poolAdmin);
    }

    function removePoolAdmin(address poolAdmin) external override {
        revokeRole(POOL_ADMIN_ROLE, poolAdmin);
    }

    function addLiquidationOperator(address address_) external override {
        grantRole(LIQUIDATION_OPERATOR, address_);
    }

    function isLiquidationOperator(address address_) external view override returns (bool) {
        return hasRole(LIQUIDATION_OPERATOR, address_);
    }

    function removeLiquidationOperator(address address_) external override {
        revokeRole(LIQUIDATION_OPERATOR, address_);
    }

    function addAirdropOperator(address address_) external override {
        grantRole(AIRDROP_OPERATOR, address_);
    }

    function isAirdropOperator(address address_) external view override returns (bool) {
        return hasRole(AIRDROP_OPERATOR, address_);
    }

    function removeAirdropOperator(address address_) external override {
        revokeRole(AIRDROP_OPERATOR, address_);
    }

}

